import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  carts: [],
};

//card slice

const cartSlice = createSlice({
  name: "cartslice",
  initialState,
  reducers: {
    //add to cart
    addToCart: (state, action) => {
      state.carts = [...state.carts, action.payload];
    },
  },
});

export const { addToCart } = cartSlice.actions;

export default cartSlice.reducer;
